(798, 1, '2016-09-15 07:40:03', '2016-09-15 07:40:03', '<table class="no-border" style="width: 1365px;">\r\n<tbody>\r\n<tr>\r\n<td style="width: 626.533px;" valign="top"><img class="aligncenter size-full wp-image-453" src="http://devsite.thucphamhanoi.com.vn/wp-content/uploads/2016/07/Nem-thit.png" alt="Nem thit" width="500" height="326" /></td>\r\n<td style="width: 716.467px;" valign="top"><strong><em><u>Sản phẩm Nem thịt</u></em></strong> và <strong><em><u>Nem thịt đặc biệt</u></em></strong> của công ty là sản phẩm có chất lượng cao, được sản xuất từ các loại nguyên liệu được kiểm tra chất lượng chặt chẽ đảm bảo ATTP trước khi đưa vào sử dụng, và được kiểm soát trong quá trình sản xuất đảm bảo chất lượng sản phẩm.\r\n\r\nSản phẩm khi sử dụng có độ giòn của vỏ bánh, độ ngọt béo, và vị bùi của thịt và rau củ, mùi  thơm của của gia vị, tất cả tạo nên hương vị đặc trưng của sản phẩm.\r\n\r\nSản phẩm của công ty không sử dụng chất bảo quản, tuyệt đối an toàn cho sức khỏe người tiêu dùng.\r\n\r\nNem thịt và Nem thịt đặc biệt có thể sử dụng như một món ăn chính trong bữa ăn hoặc ăn kèm các loại thực phẩm khác.\r\n\r\nSản phẩm Nem thịt và Nem thịt đặc biệt của công ty Thực phẩm Hà Nội là một món ăn ngon bình dân nhưng mang đậm truyền thống phù hợp cho mọi bữa ăn trong ngày. Khách hàng có thể tìm thấy tại hệ thống bán lẻ của công ty Thực phẩm Hà Nội cũng như trong hệ thống Hapromart, Haprofood của Tổng công ty Thương Mại Hà Nội</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n\r\n<hr />\r\n\r\n<h3>Thông số sản phẩm</h3>\r\n<table>\r\n<tbody>\r\n<tr>\r\n<td valign="top">Hướng dẫn bảo quản</td>\r\n<td valign="top">sản phẩm được bảo quản tốt nhất trong tủ lạnh, nhiệt độ luôn đảm bảo dưới 5 độ c</td>\r\n</tr>\r\n<tr>\r\n<td valign="top">Quy cách đóng gói</td>\r\n<td valign="top">12 chiếc/gói</td>\r\n</tr>\r\n<tr>\r\n<td valign="top">Hướng dẫn sử dụng</td>\r\n<td valign="top">giã đông 10-15 phút ,hấp nóng..</td>\r\n</tr>\r\n<tr>\r\n<td valign="top">Thành phần nguyên liệu</td>\r\n<td valign="top">bột mì, nước sạch , đường kính...</td>\r\n</tr>\r\n<tr>\r\n<td valign="top">Hạn sử dụng</td>\r\n<td valign="top">44 ngày kể từ ngày sản xuất in trên bao bì</td>\r\n</tr>\r\n<tr>\r\n<td valign="top">Khối lượng</td>\r\n<td valign="top">300 gam</td>\r\n</tr>\r\n<tr>\r\n<td valign="top">Xuất xứ</td>\r\n<td valign="top">Việt Nam</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n&nbsp;', 'Nem thịt đặc biệt', '', 'publish', 'open', 'open', '', 'nem-thit-dac-biet', '', '', '2016-09-15 07:44:49', '2016-09-15 07:44:49', '', 0, 'http://devsite.thucphamhanoi.com.vn/?p=798', 0, 'post', '', 0),
